import React, { useContext } from "react";
import { Crt } from "../Context";
import Navbar from "../Navbar/Navbar";
import "./Cart.css";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
function Cartpage() {
  const { Cart, setCart } = useContext(Crt);
  return (
    <>
      <div className="main_Nav">
        <Navbar />
      </div>
      <section className="table_main">
        <table>
          <thead>
            <tr>
              <td>Remove</td>
              <td>Image</td>
              <td>Product</td>
              <td>Price</td>
              <td>Quantity</td>
              <td>Subtotal</td>
            </tr>
          </thead>
          <tbody>
            {Cart.map((product) => (
              <tr>
                <td>
                  <DeleteOutlineIcon />
                </td>
                <td>
                  <img src={product.img} alt="image error" />{" "}
                </td>
                <td>
                {product.name}
                </td>
                <td>
                {product.price}
                </td>
                <td>
                <input></input>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
      <br />
    </>
  );
}

export default Cartpage;
