import React, { useState } from "react";
import { NavLink, useSearchParams } from "react-router-dom";
import "./Navbar.css";
import { Box } from "@mui/system";
import Cara from "../../img/Cara.png";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";
function Navbar() {
  const [isActive, setactive] = useState(false);
  

  return (
    <>
      <section className="Navbar_main">
        <img src={Cara} alt="erroe" className="Navbar_logo" />
        <div className={isActive ? "Navbar_items active" : "Navbar_items"}>
          <NavLink
            to="/"
            style={{ border: getactivepage("/") }}
            className="Navbar_links "
          >
            Home
          </NavLink>
          <NavLink
            to="/About"
            style={{ border: getactivepage("/About") }}
            className="Navbar_links"
          >
            About
          </NavLink>
          <NavLink
            to="/Blog"
            style={{ border: getactivepage("/Blog") }}
            className="Navbar_links"
          >
            Blog
          </NavLink>
          <NavLink
            to="/Shop"
            style={{ border: getactivepage("/Shop") }}
            className="Navbar_links active_link"
          >
            Shop
          </NavLink>
          <NavLink
            to="/Contact"
            style={{ border: getactivepage("/Contact ") }}
            className="Navbar_links"
          >
            Contact
          </NavLink>
          <Link
            to="/Cart"
            style={{ border: getactivepage("/Cart") }}
            className="Navbar_links "
          >
            <ShoppingCartIcon className="Navbar_cart" id="cart" />
          </Link>
          <CloseIcon
            className="close_icon"
            id="close_icon"
            onClick={() => {
              setactive(!isActive);
            }}
          />
        </div>
        <div id="id">
          <MenuIcon
            onClick={() => {
              setactive(!isActive);
            }}
            className="bar"
          />
          <ShoppingCartIcon className="Navbar_car" />
        </div>
      </section>
    </>
  );
}

export default Navbar;
