import React, { useState } from "react";
import { Link, useSearchParams } from "react-router-dom";
import "./Navbar.css";
import { Box } from "@mui/system";
import Cara from "../../img/Cara.png";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";
function Navbar() {
  const [isActive, setactive] = useState(false);
  const getactivepage=(curr)=>{
if(history.location.pathname===curr)
{
  return 'rgb(222, 28, 18)'
}
  }

  return (
    <>
      <section className="Navbar_main">
        <img src={Cara} alt="erroe" className="Navbar_logo" />
        <div className={isActive ? "Navbar_items active" : "Navbar_items"}>
          <Link to="/" className="Navbar_links ">
            Home
          </Link>
          <Link to="/About" className="Navbar_links">
            About
          </Link>
          <Link to="/Blog" className="Navbar_links">
            Blog
          </Link>
          <Link to="/Shop" className="Navbar_links active_link">
            Shop
          </Link>
          <Link to="/Contact" className="Navbar_links">
            Contact
          </Link>
          <Link to="/Cart" className="Navbar_links ">
            <ShoppingCartIcon className="Navbar_cart" id="cart" />
          </Link>
          <CloseIcon
            className="close_icon"
            id="close_icon"
            onClick={() => {
              setactive(!isActive);
            }}
          />
        </div>
        <div id="id">
          <MenuIcon
            onClick={() => {
              setactive(!isActive);
            }}
            className="bar"
          />
          <ShoppingCartIcon className="Navbar_car" />
        </div>
      </section>
    </>
  );
}

export default Navbar;
