import React, { useContext, useState } from "react";
import { useParams } from "react-router";

import "./ProductmainLayout.css";
import Navbar from "../Navbar/Navbar";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
//import Cart from "../Cart/Cart";
import { CartState } from "../context/Context";

//import { addItem, delItem } from "../../Redux/actions/index";
function ProductmainLayout() {

  const Products = [
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRWwUVRtBAV93kMpJRtvsHDbppwh8-m52EBtw&usqp=CAU",
      name: "vishal",
      id: 1,
      descripton: "Black-shirt",
      price: 550,
      rating: 3,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRbogcvouUN-Y-9uIuSTVQfowzFmNjxdlYBXw&usqp=CAU",
      name: "vishal",
      id: 2,
      descripton: "Black-shirt",
      price: 550,
      rating: 3,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRvLVecLrvDHO9b60yPV0wZwK_DIaXmjUf-SA&usqp=CAU",
      name: "vishal",
      id: 3,
      descripton: "Black-shirt",
      price: 550,
      rating: 3,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR8TpyMGPuxvXlyZiuK0NrZRpp2nEHwTux2vA&usqp=CAU",
      name: "vishal",
      id: 4,
      descripton: "Black-shirt",
      price: 550,
      rating: 1,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS9AhhWFcL0j93sYmvh7BqWUGGhtZJytz0GQA&usqp=CAU",
      name: "vishal",
      id: 5,
      descripton: "Black-shirt",
      price: 550,
      rating: 4,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUGv1eGFX4F9FmtoRNhups0-fPsSMtQQ8CcQ&usqp=CAU",
      name: "vishal",
      id: 6,
      descripton: "Black-shirt",
      price: 550,
      rating: 5,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTKhI-Nje9F3K4lozIfbmbao-0cjaXXiLOyOQ&usqp=CAU",
      name: "vishal",
      id: 7,
      descripton: "Black-shirt",
      price: 550,
      rating: 4,
      length: 1,
    },
    {
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQuJKVE8Ha1FDo1QWB3LM0lI2I6CBd1H11Jyw&usqp=CAU",
      name: "vishal",
      id: 8,
      descripton: "Black-shirt",
      price: 550,
      rating: 3,
      length: 1,
    },
  ];


const{
  state:{cart},dispatch}=CartState();

  const proid = useParams(); //now we need a product id which we passsed fro the product page
  const prodetail = Products.filter((x) => x.id == proid.id);
  const product = prodetail[0];

  

  return (
    <>
      <div className="main_Nav">
        <Navbar />
      </div>
      <section className="singleProimg">
        <div className="img">
          <img src={product.img} alt="image error" />
        </div>
        <div className="singleProDetails">
          <h4>{product.name}</h4>
          <h5>$ {product.price}</h5>
          <select placeholder="select size">
            <option defaultValue value="XL">
              XL
            </option>
            <option value="XXL">XXL</option>
            <option value="small">small</option>
            <option value="Large">Large</option>
          </select>
          <div className="counter">
            <RemoveIcon className="removeicon" />

            <input
              type="number"
              onChange={(e) => {
                dispatch({
                  type: "CHANGE_CART_QTY",
                  payload: {
                    id: Products.id,
                    qty: e.target.value,
                  },
                });
              }}
            ></input>
            <AddIcon className="addicon" />
          </div>
          {cart.some((p) => p.id ==product.id) ? (
            <button
              onClick={() => {
                dispatch({
                  type: "ADD_TO_CART",
                  payload: product,
                });
              }}
            >
            Add to cart
            </button>
          ) : (
            <button
              onClick={() => {
                dispatch({
                  type: "REMOVE_FROM_CART",
                  payload: product,
                });
              }}
            >
              Remove from Cart
            </button>
          )}

          <h4>Product Details</h4>
          <span className="desc">
            Spider-Man started off as the friendly neighbourhood
            <br />
            superhero, but soon became an important member of the Avengers
            Initiative.
            <br />
            He has dealt with threats from far off worlds.
          </span>
        </div>
      </section>
    </>
  );
}

export default ProductmainLayout;
