import React, { useState } from "react";
import { useParams } from "react-router";
import { Products } from "./Product";
import "./Product_main_layout.css";
import Navbar from "../Navbar/Navbar";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
function Product_main_layout() {
  const proid = useParams(); //now we need a product id which we passsed fro the product page
  const prodetail = Products.filter((x) => x.id == proid.id);
  const product = prodetail[0];

  const [val, setval] = useState("");
  const [addiitem, setadd] = useState(0);
  const add = () => {
    setadd(addiitem + 1);
  };
  const removef = () => {
    if (addiitem > 0) {
      setadd(addiitem - 1);
    }
  };
  const change = (event) => {
    setval(event.target.value);
  };
  return (
    <>
      <div className="main_Nav">
        <Navbar />
      </div>
      <section className="singleProimg">
        <div className="img">
          <img src={product.img} alt="image error" />
        </div>
        <div className="singleProDetails">
          <h4>{product.name}</h4>
          <h5>$ {product.price}</h5>
          <select onChange={change} value={val} placeholder="select size">
            <option selected value="XL">
              XL
            </option>
            <option value="XXL">XXL</option>
            <option value="small">small</option>
            <option value="Large">Large</option>
          </select>
          <div className="removei">
            <RemoveIcon onClick={removef} className="removeicon" />
          </div>
          <input type="number" value={addiitem}></input>
          <div>
            <AddIcon onClick={add} className="addicon" />
          </div>
          <button>Add to Cart</button>
          <h4>Product Details</h4>
          <span className="desc">
            Spider-Man started off as the friendly neighbourhood
            <br />
            superhero, but soon became an important member of the Avengers
            Initiative.
            <br />
            He has dealt with threats from far off worlds.
          </span>
        </div>
      </section>
    </>
  );
}

export default Product_main_layout;
