import React, { useState } from "react";
import Product_main_layout from "./Product_main_layout";
//import Product from "./Product";
import { useParams } from "react-router";
import "./product_details.css";
import { NavLink } from "react-router-dom";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";

function Products({ product }) {
  const [curr, setcurr] = useState([]);

  const fetchcartdetails = (event) => {
    setcurr(event.target.id);
    console.log(curr);
    console.log;
  };

  return (
    <>
      <NavLink to={`/${product.id}`}>
        <div className="Card">
          <img src={product.img} className="image" />

          <h3 className="name">{product.name}</h3>
          <p className="description">{product.description}</p>
          <h5 className="price">${product.price}</h5>
          <div className="product_rating">
            {Array(product.ratings)
              .fill()
              .map((_) => (
                <p>‚≠ê</p>
              ))}
          </div>
          <ShoppingCartIcon className="cart_icon" onClick={fetchcartdetails} />
        </div>
      </NavLink>
    </>
  );
}

export default Products;
